    <form
      onSubmit={() => {
        onSearch(city);
      }}
    >
      <label>
        Name:
        <input
          type="text"
          value={city}
          onChange={(e) => {
            setCity(e.value);
          }}
        />
      </label>
      <input type="submit" value="Submit" />
    </form>
